import React, { useState, useContext } from "react";
import { useNavigate } from "react-router-dom";
import { AuthContext } from "./context/AuthContext";
import "./expenses.css";
import axios from "axios";

const Expenses = () => {
  const navigate = useNavigate();
  const { currentUser } = useContext(AuthContext);
  const [challengeAmount, setChallengeAmount] = useState("");
  const [duration, setDuration] = useState("1 Week");
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");

  // Redirect to login if not authenticated
  if (!currentUser) {
    navigate("/signin");
    return null;
  }

  const handleAmountChange = (e) => {
    // Only allow numbers and decimal point
    const value = e.target.value.replace(/[^0-9.]/g, '');
    setChallengeAmount(value);
  };

  const handleDurationChange = (e) => {
    setDuration(e.target.value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!challengeAmount) {
      setError("Please enter a challenge amount");
      return;
    }

    setIsSubmitting(true);
    setError("");
    
    try {
      // Convert to number and validate
      const amount = parseFloat(challengeAmount);
      if (isNaN(amount) || amount <= 0) {
        throw new Error("Please enter a valid amount");
      }
      
      // Send challenge data to backend
      await axios.post('/users/challenge', {
        amount,
        duration
      }, {
        headers: {
          Authorization: `Bearer ${currentUser.token}`
        }
      });
      
      setSuccess("Challenge set successfully!");
      setChallengeAmount("");
      
      // Redirect to dashboard after a short delay
      setTimeout(() => {
        navigate('/dashboard');
      }, 2000);
      
    } catch (error) {
      console.error("Error setting challenge:", error);
      setError(error.response?.data?.error || error.message || "Failed to set challenge");
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <div className="challenge-container">
      <div className="challenge-card">
        <h1>SET CHALLENGE:</h1>
        
        {error && <div className="error-message">{error}</div>}
        {success && <div className="success-message">{success}</div>}
        
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label htmlFor="amount">Amount:</label>
            <div className="amount-input">
              <span className="dollar-sign">$</span>
              <input
                type="text"
                id="amount"
                placeholder="Enter amount"
                value={challengeAmount}
                onChange={handleAmountChange}
                disabled={isSubmitting}
              />
            </div>
          </div>
          
          <div className="form-group">
            <label htmlFor="duration">Duration:</label>
            <select
              id="duration"
              value={duration}
              onChange={handleDurationChange}
              disabled={isSubmitting}
            >
              <option value="1 Week">1 Week</option>
              <option value="2 Weeks">2 Weeks</option>
              <option value="1 Month">1 Month</option>
              <option value="3 Months">3 Months</option>
            </select>
          </div>
          
          <button 
            type="submit" 
            className="set-challenge-btn"
            disabled={isSubmitting}
          >
            {isSubmitting ? "Setting Challenge..." : "Set Challenge"}
          </button>
        </form>
      </div>
    </div>
  );
};

export default Expenses;
