import React, { useState, useContext } from "react";
import { useNavigate } from "react-router-dom";
import { AuthContext } from "./context/AuthContext";
import "./expenses.css";
import axios from "axios";

const Expenses = () => {
  const navigate = useNavigate();
  const { currentUser } = useContext(AuthContext);
  const [expenses, setExpenses] = useState({
    food: "",
    transportation: "",
    entertainment: "",
    essentials: "",
  });

  // Redirect to login if not authenticated
  if (!currentUser) {
    navigate("/signin");
    return null;
  }

  const handleChange = (e) => {
    setExpenses({ ...expenses, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    
    // Create individual expense entries for each category
    const createExpensePromises = [];
    
    if (expenses.food && parseFloat(expenses.food) > 0) {
      createExpensePromises.push(
        axios.post('/expenses/add', {
          userId: currentUser._id,
          amount: parseFloat(expenses.food),
          category: "Food",
          description: "Food expense"
        })
      );
    }
    
    if (expenses.transportation && parseFloat(expenses.transportation) > 0) {
      createExpensePromises.push(
        axios.post('/expenses/add', {
          userId: currentUser._id,
          amount: parseFloat(expenses.transportation),
          category: "Transportation",
          description: "Transportation expense"
        })
      );
    }
    
    if (expenses.entertainment && parseFloat(expenses.entertainment) > 0) {
      createExpensePromises.push(
        axios.post('/expenses/add', {
          userId: currentUser._id,
          amount: parseFloat(expenses.entertainment),
          category: "Entertainment",
          description: "Entertainment expense"
        })
      );
    }
    
    if (expenses.essentials && parseFloat(expenses.essentials) > 0) {
      createExpensePromises.push(
        axios.post('/expenses/add', {
          userId: currentUser._id,
          amount: parseFloat(expenses.essentials),
          category: "Essentials",
          description: "Essential expense"
        })
      );
    }
    
    // Submit all expenses
    Promise.all(createExpensePromises)
      .then(responses => {
        alert("Expenses added successfully!");
        navigate("/dashboard");
      })
      .catch(error => {
        console.error("Error adding expense:", error);
        alert("Failed to add expense. Please try again.");
      });
  };

  return (
    <div className="log-expenses">
      <header>
        <h1>Log Expenses</h1>
      </header>
      <form onSubmit={handleSubmit}>
        <label>Food: $</label>
        <input type="text" name="food" value={expenses.food} onChange={handleChange} />
        
        <label>Transportation: $</label>
        <input type="text" name="transportation" value={expenses.transportation} onChange={handleChange} />
        
        <label>Entertainment: $</label>
        <input type="text" name="entertainment" value={expenses.entertainment} onChange={handleChange} />
        
        <label>Essentials: $</label>
        <input type="text" name="essentials" value={expenses.essentials} onChange={handleChange} />

        <button type="submit">Add Expense</button>
      </form>
    </div>
  );
};

export default Expenses;
