import React, { useState, useContext } from "react";
import { useNavigate } from "react-router-dom";
import { AuthContext } from "./context/AuthContext";
import "./expenses.css";
import axios from "axios";

const Expenses = () => {
  const navigate = useNavigate();
  const { currentUser } = useContext(AuthContext);
  const [expenses, setExpenses] = useState({
    food: "",
    transportation: "",
    entertainment: "",
    essentials: "",
  });
  const [challenge, setChallenge] = useState({
    amount: "",
    duration: "1 Week"
  });
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [error, setError] = useState("");
  const [activeTab, setActiveTab] = useState("expenses"); // "expenses" or "challenge"

  // Navigation handlers
  const handleDashboardClick = () => {
    navigate('/dashboard');
  };

  const handleSpendingHistoryClick = () => {
    navigate('/spendinghistory');
  };

  const handleChallengesClick = () => {
    setActiveTab("challenge");
  };

  // Redirect to login if not authenticated
  if (!currentUser) {
    navigate("/signin");
    return null;
  }

  const handleChange = (e) => {
    setExpenses({ ...expenses, [e.target.name]: e.target.value });
  };

  const handleChallengeChange = (e) => {
    setChallenge({ ...challenge, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");
    setIsSubmitting(true);

    // Validate inputs
    const expenseValues = Object.values(expenses).filter(val => val.trim() !== "");
    if (expenseValues.length === 0) {
      setError("Please enter at least one expense");
      setIsSubmitting(false);
      return;
    }

    // Create an array of expense objects to submit
    const createExpensePromises = [];

    // Only create expense objects for fields with values
    if (expenses.food && expenses.food.trim() !== "") {
      createExpensePromises.push(
        axios.post('/expenses', {
          category: "Food",
          amount: parseFloat(expenses.food),
          description: "Food expense"
        }, {
          headers: {
            Authorization: `Bearer ${currentUser.token}`
          }
        })
      );
    }

    if (expenses.transportation && expenses.transportation.trim() !== "") {
      createExpensePromises.push(
        axios.post('/expenses', {
          category: "Transportation",
          amount: parseFloat(expenses.transportation),
          description: "Transportation expense"
        }, {
          headers: {
            Authorization: `Bearer ${currentUser.token}`
          }
        })
      );
    }

    if (expenses.entertainment && expenses.entertainment.trim() !== "") {
      createExpensePromises.push(
        axios.post('/expenses', {
          category: "Entertainment",
          amount: parseFloat(expenses.entertainment),
          description: "Entertainment expense"
        }, {
          headers: {
            Authorization: `Bearer ${currentUser.token}`
          }
        })
      );
    }

    if (expenses.essentials && expenses.essentials.trim() !== "") {
      createExpensePromises.push(
        axios.post('/expenses', {
          category: "Essentials",
          amount: parseFloat(expenses.essentials),
          description: "Essential expense"
        }, {
          headers: {
            Authorization: `Bearer ${currentUser.token}`
          }
        })
      );
    }

    try {
      // Submit all expenses
      await Promise.all(createExpensePromises);

      // Reset form
      setExpenses({
        food: "",
        transportation: "",
        entertainment: "",
        essentials: ""
      });

      // Show success message or redirect
      navigate("/dashboard");
    } catch (error) {
      console.error("Error adding expenses:", error);
      setError("Failed to add expenses. Please try again.");
    } finally {
      setIsSubmitting(false);
    }
  };

  const handleChallengeSubmit = async (e) => {
    e.preventDefault();
    setError("");
    setIsSubmitting(true);

    if (!challenge.amount || parseFloat(challenge.amount) <= 0) {
      setError("Please enter a valid challenge amount");
      setIsSubmitting(false);
      return;
    }

    try {
      // Configure axios with auth header
      const config = {
        headers: {
          Authorization: `Bearer ${currentUser.token}`
        }
      };

      // Submit challenge
      await axios.post('/users/challenge', {
        amount: parseFloat(challenge.amount),
        duration: challenge.duration
      }, config);

      // Reset form
      setChallenge({
        amount: "",
        duration: "1 Week"
      });

      // Show success message or redirect
      navigate("/dashboard");
    } catch (error) {
      console.error("Error setting challenge:", error);
      setError("Failed to set challenge. Please try again.");
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <div className="dashboard">
      <aside className="sidebar">
        <div className="sidebar__brand">
          <img src={`${process.env.PUBLIC_URL}/piggyBankIcon.png`} alt="NoSpendy Logo" className="logo" />
          <h2>NoSpendy</h2>
        </div>
        <div className="sidebar__menu">
          <ul>
            <li onClick={handleDashboardClick}>Dashboard</li>
            <li onClick={handleSpendingHistoryClick}>History</li>
            <li className="active">Log Expenses</li>
            <li onClick={handleChallengesClick}>Challenges</li>
            <li>Leaderboard</li>
            <li>Overview</li>
            <li>Notifications</li>
            <li>Settings</li>
            <li>Security</li>
          </ul>
        </div>
      </aside>

      <main className="main-content">
        <header className="topbar">
          <h1>Manage Expenses</h1>
          <div className="topbar__search">
            <input type="text" placeholder="Search" />
            <button className="profile-btn">
              <img
                src={`${process.env.PUBLIC_URL}/profile_icon.png`}
                alt="Profile"
                className="profile-img"
              />
            </button>
          </div>
        </header>

        <div className="tabs">
          <button 
            className={activeTab === "expenses" ? "tab-active" : ""} 
            onClick={() => setActiveTab("expenses")}
          >
            Log Expenses
          </button>
          <button 
            className={activeTab === "challenge" ? "tab-active" : ""} 
            onClick={() => setActiveTab("challenge")}
          >
            Set Challenge
          </button>
        </div>

        {error && <p className="error-message">{error}</p>}

        {activeTab === "expenses" ? (
          <div className="expense-form-container">
            <form onSubmit={handleSubmit}>
              <div className="expense-form">
                <div className="expense-category">
                  <label htmlFor="food">Food</label>
                  <div className="expense-input">
                    <span className="currency-symbol">$</span>
                    <input
                      type="number"
                      id="food"
                      name="food"
                      placeholder="0.00"
                      value={expenses.food}
                      onChange={handleChange}
                      min="0"
                      step="0.01"
                    />
                  </div>
                </div>

                <div className="expense-category">
                  <label htmlFor="transportation">Transportation</label>
                  <div className="expense-input">
                    <span className="currency-symbol">$</span>
                    <input
                      type="number"
                      id="transportation"
                      name="transportation"
                      placeholder="0.00"
                      value={expenses.transportation}
                      onChange={handleChange}
                      min="0"
                      step="0.01"
                    />
                  </div>
                </div>

                <div className="expense-category">
                  <label htmlFor="entertainment">Entertainment</label>
                  <div className="expense-input">
                    <span className="currency-symbol">$</span>
                    <input
                      type="number"
                      id="entertainment"
                      name="entertainment"
                      placeholder="0.00"
                      value={expenses.entertainment}
                      onChange={handleChange}
                      min="0"
                      step="0.01"
                    />
                  </div>
                </div>

                <div className="expense-category">
                  <label htmlFor="essentials">Essentials</label>
                  <div className="expense-input">
                    <span className="currency-symbol">$</span>
                    <input
                      type="number"
                      id="essentials"
                      name="essentials"
                      placeholder="0.00"
                      value={expenses.essentials}
                      onChange={handleChange}
                      min="0"
                      step="0.01"
                    />
                  </div>
                </div>
              </div>

              <button 
                type="submit" 
                className="submit-button"
                disabled={isSubmitting}
              >
                {isSubmitting ? "Submitting..." : "Log Expenses"}
              </button>
            </form>
          </div>
        ) : (
          <div className="challenge-container">
            <h2 className="challenge-title">SET CHALLENGE:</h2>
            <form className="challenge-form" onSubmit={handleChallengeSubmit}>
              <div>
                <label htmlFor="amount">Amount:</label>
                <input
                  type="number"
                  id="amount"
                  name="amount"
                  placeholder="Enter challenge amount"
                  value={challenge.amount}
                  onChange={handleChallengeChange}
                  min="0"
                  step="0.01"
                  required
                />
              </div>

              <div>
                <label htmlFor="duration">Duration:</label>
                <select
                  id="duration"
                  name="duration"
                  value={challenge.duration}
                  onChange={handleChallengeChange}
                >
                  <option value="1 Week">1 Week</option>
                  <option value="2 Weeks">2 Weeks</option>
                  <option value="1 Month">1 Month</option>
                  <option value="3 Months">3 Months</option>
                </select>
              </div>

              <button 
                type="submit" 
                className="challenge-button"
                disabled={isSubmitting}
              >
                {isSubmitting ? "Setting Challenge..." : "Set Challenge"}
              </button>
            </form>
          </div>
        )}
      </main>
    </div>
  );
};

export default Expenses;
