import React, { useState, useContext } from "react";
import { useNavigate } from "react-router-dom";
import { AuthContext } from "./context/AuthContext";
import "./signin.css";
import piggyBankIcon from "./piggyBankIcon.png"; // Add the piggy bank icon image file
import axios from "axios";

const Signin = () => {
    const navigate = useNavigate();
    const { login, setCurrentUser } = useContext(AuthContext);
    const [formData, setFormData] = useState({
    username: "",
    password: "",
    });
    const [error, setError] = useState("");
    const [loading, setLoading] = useState(false);

    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);
        setError(null);
        
        try {
            // Clear any existing auth data first
            localStorage.removeItem('user');
            localStorage.removeItem('token');
            
            // Attempt to log in
            const response = await axios.post('/users/login', {
                username: formData.username,
                password: formData.password
            });
            
            // Store the user data in localStorage
            const userData = response.data;
            localStorage.setItem('user', JSON.stringify(userData));
            
            // Update the auth context
            setCurrentUser(userData);
            
            // Redirect to dashboard
            navigate('/dashboard');
        } catch (err) {
            console.error('Login error:', err);
            
            // Show appropriate error message
            if (err.response && err.response.status === 401) {
                setError('Invalid username or password');
            } else if (err.response && err.response.data && err.response.data.error) {
                setError(err.response.data.error);
            } else {
                setError('An error occurred during sign in. Please try again.');
            }
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="signin-container">
        <div className="signin-header">
            <img src={piggyBankIcon} alt="NoSpendy Logo" className="logo" />
            <h1>NoSpendy</h1>
        </div>

        <div className="signin-box">
            <h2>Sign In</h2>
            {error && <p className="error-message">{error}</p>}
            <form onSubmit={handleSubmit}>
            <label>Username:</label>
            <input
                type="text"
                name="username"
                value={formData.username}
                onChange={handleChange}
                required
            />

            <label>Password:</label>
            <input
                type="password"
                name="password"
                value={formData.password}
                onChange={handleChange}
                required
            />

            <button type="submit">Sign In</button>
            </form>

            <div className="signin-links">
            <a href="/forgot-password">Forgot Password</a>
            <a href="/createprofile">Create New Account</a>
            </div>
        </div>
        </div>
    );
};

export default Signin;
