import React, { useState, useContext } from "react";
import { useNavigate } from "react-router-dom";
import { AuthContext } from "./context/AuthContext";
import "./signin.css";
import piggyBankIcon from "./piggyBankIcon.png"; // Add the piggy bank icon image file
import axios from "axios";

const Signin = () => {
    const navigate = useNavigate();
    const { login, setCurrentUser } = useContext(AuthContext);
    const [formData, setFormData] = useState({
    username: "",
    password: "",
    });
    const [error, setError] = useState("");
    const [loading, setLoading] = useState(false);

    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    // Add this function to clear all auth data before attempting login
    const clearAuthData = () => {
        console.log("Clearing all auth data...");
        localStorage.removeItem('user');
        localStorage.removeItem('token');
        sessionStorage.removeItem('user');
        sessionStorage.removeItem('token');
        
        // Clear any cookies related to authentication
        document.cookie.split(";").forEach(function(c) {
            document.cookie = c.replace(/^ +/, "").replace(/=.*/, "=;expires=" + new Date().toUTCString() + ";path=/");
        });
        
        console.log("All auth data cleared");
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);
        setError(null);
        
        // Clear all auth data first
        clearAuthData();
        
        console.log("=== SIGN IN ATTEMPT ===");
        console.log("Username:", formData.username);
        console.log("Password length:", formData.password.length);
        
        try {
            // Try using the context login function first
            console.log("Attempting login via AuthContext...");
            try {
                const user = await login(formData.username, formData.password);
                console.log("AuthContext login successful:", user);
                navigate('/dashboard');
                return; // Exit if successful
            } catch (loginError) {
                console.error("AuthContext login failed:", loginError);
                console.log("Falling back to direct API calls...");
            }
            
            // Direct API call as fallback
            try {
                console.log("Attempting direct API call to /users/login...");
                const response = await axios.post('/users/login', {
                    username: formData.username,
                    password: formData.password
                });
                
                console.log("Direct API call successful:", response.data);
                
                // Store the user data in localStorage
                const userData = response.data;
                localStorage.setItem('user', JSON.stringify(userData));
                console.log("User data stored in localStorage");
                
                // Update the auth context
                setCurrentUser(userData);
                console.log("Auth context updated with user data");
                
                // Redirect to dashboard
                navigate('/dashboard');
            } catch (directApiError) {
                console.error("Direct API call failed:", directApiError);
                
                // Try with full URL if relative URL fails
                console.log("Attempting API call with full URL...");
                
                const baseUrl = process.env.REACT_APP_API_URL || '';
                console.log("Using base URL:", baseUrl);
                
                const fullResponse = await axios.post(`${baseUrl}/users/login`, {
                    username: formData.username,
                    password: formData.password
                });
                
                console.log("Full URL API call successful:", fullResponse.data);
                
                const userData = fullResponse.data;
                localStorage.setItem('user', JSON.stringify(userData));
                console.log("User data stored in localStorage");
                
                setCurrentUser(userData);
                console.log("Auth context updated with user data");
                
                navigate('/dashboard');
            }
        } catch (err) {
            console.error("=== SIGN IN FAILED ===");
            console.error("Error type:", err.constructor.name);
            console.error("Error message:", err.message);
            console.error("Error stack:", err.stack);
            
            // Show appropriate error message with more details
            if (err.response) {
                console.error("Error response status:", err.response.status);
                console.error("Error response headers:", err.response.headers);
                console.error("Error response data:", err.response.data);
                
                if (err.response.status === 401) {
                    setError('Invalid username or password');
                } else if (err.response.data && err.response.data.error) {
                    setError(err.response.data.error);
                } else {
                    setError(`Server error: ${err.response.status}`);
                }
            } else if (err.request) {
                // Request was made but no response received
                console.error("Error request:", err.request);
                setError('No response from server. Please check your connection.');
            } else {
                // Something else caused the error
                setError(`Error: ${err.message}`);
            }
        } finally {
            setLoading(false);
            console.log("=== SIGN IN ATTEMPT COMPLETED ===");
        }
    };

    return (
        <div className="signin-container">
        <div className="signin-header">
            <img src={piggyBankIcon} alt="NoSpendy Logo" className="logo" />
            <h1>NoSpendy</h1>
        </div>

        <div className="signin-box">
            <h2>Sign In</h2>
            {error && <p className="error-message">{error}</p>}
            <form onSubmit={handleSubmit}>
            <label>Username:</label>
            <input
                type="text"
                name="username"
                value={formData.username}
                onChange={handleChange}
                required
            />

            <label>Password:</label>
            <input
                type="password"
                name="password"
                value={formData.password}
                onChange={handleChange}
                required
            />

            <button type="submit" disabled={loading}>
                {loading ? 'Signing in...' : 'Sign In'}
            </button>
            </form>

            <div className="signin-links">
            <a href="/forgot-password">Forgot Password</a>
            <a href="/createprofile">Create New Account</a>
            </div>
        </div>
        </div>
    );
};

export default Signin;
