import React, { useState, useEffect, useContext } from "react";
import { AuthContext } from "./context/AuthContext";
import { Link } from "react-router-dom";
import axios from "axios";
import "./dashboard.css";

const Dashboard = () => {
  const { currentUser } = useContext(AuthContext);
  const [dashboardData, setDashboardData] = useState({
    totalExpenses: 0,
    spendingChallenge: 0,
    spentThisMonth: 0,
    streak: 0,
    loading: true,
    error: null
  });
  const [expenses, setExpenses] = useState([]);
  const [expensesLoading, setExpensesLoading] = useState(true);

  useEffect(() => {
    const fetchDashboardData = async () => {
      try {
        // Get monthly expenses
        const monthlyResponse = await axios.get('/expenses/monthly', {
          headers: {
            Authorization: `Bearer ${currentUser.token}`
          }
        });

        // Get all expenses for total
        const allExpensesResponse = await axios.get('/expenses/all', {
          headers: {
            Authorization: `Bearer ${currentUser.token}`
          }
        });

        // Get user profile for savings goal and streak
        const userProfileResponse = await axios.get('/users/profile', {
          headers: {
            Authorization: `Bearer ${currentUser.token}`
          }
        });

        // Calculate total expenses
        const totalExpenses = allExpensesResponse.data.reduce(
          (sum, expense) => sum + expense.amount, 
          0
        );

        // Get monthly spending
        const spentThisMonth = monthlyResponse.data.totalSpent;

        // Get user's savings goal (spending challenge)
        const spendingChallenge = userProfileResponse.data.savingsGoal || 0;

        // Get user's streak
        const streak = userProfileResponse.data.streak || 0;

        // Set expenses for history section
        setExpenses(allExpensesResponse.data);
        setExpensesLoading(false);

        setDashboardData({
          totalExpenses,
          spendingChallenge,
          spentThisMonth,
          streak,
          loading: false,
          error: null
        });
      } catch (error) {
        console.error("Error fetching dashboard data:", error);
        setDashboardData(prev => ({
          ...prev,
          loading: false,
          error: "Failed to load dashboard data"
        }));
        setExpensesLoading(false);
      }
    };

    if (currentUser) {
      fetchDashboardData();
    }
  }, [currentUser]);

  // Format date for expense history
  const formatDate = (dateString) => {
    const options = { year: 'numeric', month: 'short', day: 'numeric' };
    return new Date(dateString).toLocaleDateString(undefined, options);
  };

  if (dashboardData.loading) {
    return <div className="dashboard-loading">Loading dashboard data...</div>;
  }

  return (
    <div className="dashboard">
      <div className="sidebar">
        <h2>NoSpendy</h2>
        <ul>
          <li className="active">Dashboard</li>
          <li><Link to="/expenses">Log Expenses</Link></li>
        </ul>
      </div>
      
      <div className="main-content">
        <div className="topbar">
          <h1>Dashboard</h1>
          <div className="search">
            <input type="text" placeholder="Search..." />
          </div>
        </div>
        
        {dashboardData.error && (
          <div className="error-message">{dashboardData.error}</div>
        )}
        
        <div className="top-stats">
          <div className="stat-card">
            <h3>Total Expenses</h3>
            <div className="stat-amount">${dashboardData.totalExpenses.toFixed(2)}</div>
          </div>
          
          <div className="stat-card">
            <h3>Spending Challenge</h3>
            <div className="stat-amount">${dashboardData.spendingChallenge.toFixed(2)}</div>
          </div>
          
          <div className="stat-card">
            <h3>Spent This Month</h3>
            <div className="stat-amount">${dashboardData.spentThisMonth.toFixed(2)}</div>
          </div>
          
          <div className="stat-card">
            <h3>Streak</h3>
            <div className="stat-amount">{dashboardData.streak} Days</div>
          </div>
        </div>

        {/* Spending History Section */}
        <div className="spending-history-section">
          <h2>Spending History</h2>
          
          {expensesLoading ? (
            <p>Loading expense history...</p>
          ) : expenses.length === 0 ? (
            <div className="no-expenses">
              <p>No expenses recorded yet.</p>
              <Link to="/expenses" className="action-button">Log Your First Expense</Link>
            </div>
          ) : (
            <div className="expense-history">
              <table className="expense-table">
                <thead>
                  <tr>
                    <th>Date</th>
                    <th>Category</th>
                    <th>Description</th>
                    <th>Amount</th>
                  </tr>
                </thead>
                <tbody>
                  {expenses.map(expense => (
                    <tr key={expense._id}>
                      <td>{formatDate(expense.createdAt)}</td>
                      <td>{expense.category}</td>
                      <td>{expense.description || '-'}</td>
                      <td className="amount">${expense.amount.toFixed(2)}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          )}
        </div>
        
        <div className="dashboard-actions">
          <Link to="/expenses" className="action-button">
            Log New Expense
          </Link>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
