import React, { useState, useContext } from "react";
import { useNavigate } from "react-router-dom";
import { AuthContext } from "./context/AuthContext";
import "./expenses.css";
import axios from "axios";

const Expenses = () => {
  const navigate = useNavigate();
  const { currentUser } = useContext(AuthContext);
  const [expenses, setExpenses] = useState({
    food: "",
    transportation: "",
    entertainment: "",
    essentials: "",
  });
  const [challenge, setChallenge] = useState({
    amount: "",
    duration: "1 Week"
  });
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [error, setError] = useState("");
  const [activeTab, setActiveTab] = useState("expenses"); // "expenses" or "challenge"
  const [success, setSuccess] = useState(false);

  // Navigation handlers
  const handleDashboardClick = () => {
    navigate('/dashboard');
  };

  const handleSpendingHistoryClick = () => {
    navigate('/dashboard'); // For now, navigate to dashboard since history is there
  };

  const handleChallengesClick = () => {
    setActiveTab("challenge");
  };

  const handleLeaderboardClick = () => {
    navigate('/leaderboard');
  };

  // Redirect to login if not authenticated
  if (!currentUser) {
    navigate("/signin");
    return null;
  }

  const handleExpenseChange = (category, value) => {
    // Only allow numbers and decimal point
    const validatedValue = value.replace(/[^0-9.]/g, '');

    // Prevent multiple decimal points
    if (validatedValue.split('.').length > 2) {
      return;
    }

    // Update the expenses state
    setExpenses({
      ...expenses,
      [category]: validatedValue
    });
  };

  const handleChallengeChange = (e) => {
    setChallenge({ ...challenge, [e.target.name]: e.target.value });
  };

  const handleAddExpenses = async (e) => {
    // Prevent default form submission behavior
    if (e) e.preventDefault();

    console.log("=== EXPENSE SUBMISSION FUNCTION CALLED ===");
    console.log("Event object:", e);

    try {
      setIsSubmitting(true);
      setError(null);
      setSuccess(false);

      // Log the current state of the component
      console.log("Component state at submission:");
      console.log("- expenses:", expenses);
      console.log("- isSubmitting:", isSubmitting);
      console.log("- error:", error);
      console.log("- success:", success);
      console.log("- activeTab:", activeTab);

      // Validate expenses
      const totalExpenses = Object.values(expenses).reduce((sum, value) => {
        const parsedValue = parseFloat(value || 0);
        console.log(`Parsing expense value: "${value}" to ${parsedValue}`);
        return sum + parsedValue;
      }, 0);

      console.log("Total expenses calculated:", totalExpenses);

      if (totalExpenses <= 0) {
        console.log("Validation failed: No expenses entered");
        setError("Please enter at least one expense amount");
        setIsSubmitting(false);
        return;
      }

      // Prepare expense data
      const expenseData = [];

      Object.entries(expenses).forEach(([category, amount]) => {
        const parsedAmount = parseFloat(amount || 0);
        console.log(`Processing category: ${category}, amount: ${amount}, parsed: ${parsedAmount}`);

        if (parsedAmount > 0) {
          expenseData.push({
            category,
            amount: parsedAmount,
            date: new Date().toISOString()
          });
        }
      });

      console.log("Formatted expense data:", expenseData);

      if (expenseData.length === 0) {
        console.log("No valid expenses to submit");
        setError("Please enter valid expense amounts");
        setIsSubmitting(false);
        return;
      }

      // Get auth token
      const userStr = localStorage.getItem('user');
      console.log("User data from localStorage:", userStr);

      if (!userStr) {
        console.log("No user data found in localStorage");
        setError("Authentication error. Please sign in again.");
        setIsSubmitting(false);
        return;
      }

      const user = JSON.parse(userStr);
      console.log("Parsed user data:", user);

      if (!user || !user.token) {
        console.log("No token found in user data");
        setError("Authentication error. Please sign in again.");
        setIsSubmitting(false);
        return;
      }

      // Configure request headers
      const config = {
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${user.token}`
        }
      };

      console.log("Request config:", config);

      // Try multiple API endpoints
      console.log("Attempting to submit expenses...");

      // First, try with the API URL from environment variables
      const baseUrl = process.env.REACT_APP_API_URL || '';
      console.log("Base URL from environment:", baseUrl);

      // Try different API endpoints
      const endpoints = [
        '/api/expenses',
        '/expenses',
        `${baseUrl}/api/expenses`,
        `${baseUrl}/expenses`,
        'http://localhost:5000/api/expenses',
        'http://localhost:5000/expenses'
      ];

      console.log("Will try these endpoints:", endpoints);

      let success = false;
      let lastError = null;

      for (const endpoint of endpoints) {
        try {
          console.log(`Attempting to submit expenses to ${endpoint}`);
          const response = await axios.post(endpoint, expenseData, config);
          console.log(`Expense submission successful to ${endpoint}:`, response.data);

          // Reset form and show success message
          setExpenses({
            food: "",
            transportation: "",
            entertainment: "",
            essentials: ""
          });

          setSuccess(true);
          console.log("Success state set to true");
          setTimeout(() => {
            console.log("Clearing success message");
            setSuccess(false);
          }, 3000);

          success = true;
          break; // Exit the loop if successful
        } catch (error) {
          console.error(`Error with endpoint ${endpoint}:`, error.message);
          lastError = error;
          // Continue to the next endpoint
        }
      }

      if (!success) {
        // If all endpoints failed, throw the last error
        console.error("All endpoints failed. Last error:", lastError);
        throw lastError;
      }
    } catch (error) {
      console.error("=== EXPENSE SUBMISSION ERROR ===");
      console.error("Error type:", error.constructor.name);
      console.error("Error message:", error.message);

      if (error.response) {
        console.error("Error response status:", error.response.status);
        console.error("Error response data:", error.response.data);
        setError(error.response.data.error || "Failed to add expenses. Please try again.");
      } else if (error.request) {
        console.error("No response received:", error.request);
        setError("No response from server. Please check your connection.");
      } else {
        setError("Failed to add expenses. Please try again.");
      }
    } finally {
      setIsSubmitting(false);
      console.log("=== EXPENSE SUBMISSION COMPLETED ===");
    }
  };

  const handleChallengeSubmit = async (e) => {
    e.preventDefault();
    setError("");
    setIsSubmitting(true);

    if (!challenge.amount || parseFloat(challenge.amount) <= 0) {
      setError("Please enter a valid challenge amount");
      setIsSubmitting(false);
      return;
    }

    try {
      // Configure axios with auth header
      const config = {
        headers: {
          Authorization: `Bearer ${currentUser.token}`
        }
      };

      // Submit challenge
      await axios.post('/users/challenge', {
        amount: parseFloat(challenge.amount),
        duration: challenge.duration
      }, config);

      // Reset form
      setChallenge({
        amount: "",
        duration: "1 Week"
      });

      // Show success message or redirect
      navigate("/dashboard");
    } catch (error) {
      console.error("Error setting challenge:", error);
      setError("Failed to set challenge. Please try again.");
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <div className="dashboard">
      <aside className="sidebar">
        <div className="sidebar__brand">
          <img src={`${process.env.PUBLIC_URL}/piggyBankIcon.png`} alt="NoSpendy Logo" className="logo" />
          <h2>NoSpendy</h2>
        </div>
        <div className="sidebar__menu">
          <ul>
            <li onClick={handleDashboardClick}>Dashboard</li>
            <li onClick={handleSpendingHistoryClick}>History</li>
            <li className="active">Log Expenses</li>
            <li onClick={handleChallengesClick}>Challenges</li>
            <li onClick={handleLeaderboardClick}>Leaderboard</li>
            <li>Overview</li>
            <li>Notifications</li>
            <li>Settings</li>
            <li>Security</li>
          </ul>
        </div>
      </aside>

      <main className="main-content">
        <header className="topbar">
          <h1>Manage Expenses</h1>
          <div className="topbar__search">
            <input type="text" placeholder="Search" />
            <button className="profile-btn">
              <img
                src={`${process.env.PUBLIC_URL}/profile_icon.png`}
                alt="Profile"
                className="profile-img"
              />
            </button>
          </div>
        </header>

        <div className="tabs">
          <button 
            className={activeTab === "expenses" ? "tab-active" : ""} 
            onClick={() => setActiveTab("expenses")}
          >
            Log Expenses
          </button>
          <button 
            className={activeTab === "challenge" ? "tab-active" : ""} 
            onClick={() => setActiveTab("challenge")}
          >
            Set Challenge
          </button>
        </div>

        {error && <p className="error-message">{error}</p>}
        {success && <p className="success-message">Expenses added successfully!</p>}

        {activeTab === "expenses" ? (
          <div className="expense-form-container">
            <form onSubmit={handleAddExpenses} className="expense-form">
              <div className="form-group">
                <label>Food</label>
                <input 
                  type="number" 
                  name="food" 
                  value={expenses.food} 
                  onChange={(e) => handleExpenseChange("food", e.target.value)} 
                  placeholder="0.00"
                  step="0.01"
                  min="0"
                />
              </div>

              <div className="form-group">
                <label>Transportation</label>
                <input 
                  type="number" 
                  name="transportation" 
                  value={expenses.transportation} 
                  onChange={(e) => handleExpenseChange("transportation", e.target.value)} 
                  placeholder="0.00"
                  step="0.01"
                  min="0"
                />
              </div>

              <div className="form-group">
                <label>Entertainment</label>
                <input 
                  type="number" 
                  name="entertainment" 
                  value={expenses.entertainment} 
                  onChange={(e) => handleExpenseChange("entertainment", e.target.value)} 
                  placeholder="0.00"
                  step="0.01"
                  min="0"
                />
              </div>

              <div className="form-group">
                <label>Essentials</label>
                <input 
                  type="number" 
                  name="essentials" 
                  value={expenses.essentials} 
                  onChange={(e) => handleExpenseChange("essentials", e.target.value)} 
                  placeholder="0.00"
                  step="0.01"
                  min="0"
                />
              </div>

              <button 
                type="submit" 
                className="submit-button" 
                disabled={isSubmitting}
              >
                {isSubmitting ? "Adding..." : "Add Expenses"}
              </button>
            </form>
          </div>
        ) : (
          <div className="challenge-container">
            <form onSubmit={handleChallengeSubmit} className="challenge-form">
              <h2>SET CHALLENGE:</h2>

              <div className="form-group">
                <label>Amount:</label>
                <input 
                  type="number" 
                  name="amount" 
                  value={challenge.amount} 
                  onChange={handleChallengeChange} 
                  placeholder="Enter amount"
                  step="0.01"
                  min="0"
                />
              </div>

              <div className="form-group">
                <label>Duration:</label>
                <select 
                  name="duration" 
                  value={challenge.duration} 
                  onChange={handleChallengeChange}
                >
                  <option value="1 Week">1 Week</option>
                  <option value="2 Weeks">2 Weeks</option>
                  <option value="1 Month">1 Month</option>
                  <option value="3 Months">3 Months</option>
                </select>
              </div>

              <button 
                type="submit" 
                className="challenge-button" 
                disabled={isSubmitting}
              >
                Set Challenge
              </button>
            </form>
          </div>
        )}
      </main>
    </div>
  );
};

export default Expenses;
